import React, {useState} from 'react';
import { useNavigate, Link, useParams} from 'react-router-dom';
import './styles.css';

import api from '../../services/api'

import logoImage from '../../assets/logo.svg'

export default function NewPostagem() {
    
    const [nome, setNome] = useState('');
    const [descricao, setDescricao] = useState('');
    const navigate = useNavigate();

    const recebeToken = localStorage.getItem('accessToken');

    async function cadastrarPostagem(e){
        e.preventDefault();

        const data = {
                "nome": nome,
                "descricao": descricao
        };
        try {
            await api.post('postagem', data, {
                    headers: {
                        Authorization: recebeToken
                    }
                });
            
            alert('Cadastro ok')
            navigate('/postagens');
        } catch (err) {
            alert('Error while recording Book! Try again!')
        }
    };

    return (
        <div className="new-book-container">
                        <div className="content">

            <section className="form">
            <img src={logoImage} alt="Erudio"/>
                    <Link className="back-link" to="/postagens">
                        Back to Postagens
                    </Link>
                </section>

                <form onSubmit={cadastrarPostagem}>
                    <h1>Cadastrar postagem</h1>
                     <input
                        type="text" placeholder="Nome" name="Nome"
                        value={nome}
                        onChange={e => setNome(e.target.value)}
                    />
                    <input
                        type="text" placeholder="Descricao do livro"
                        value={descricao}
                        onChange={e => setDescricao(e.target.value)}
                    />
                   
                    <button className="button" type="submit">Cadastrar </button>
                </form>

            </div>

        </div>
    )

}
